fn test_server(cfg: CorsConfig) -> TestServer {
	let (chain, pipeline) = single_pipeline(new_pipeline().add(cfg).build());
	TestServer::new(build_router(chain, pipeline, |router| {
		router.resource::<FooResource>("/foo")
	}))
