fn test_response<TS, C>(
	req: TestRequest<TS, C>,
	origin: Option<&str>,
	vary: Option<&str>,
	credentials: bool
) where
	TS: Server + 'static,
	C: Connect + Clone + Send + Sync + 'static
{
	let res = req
		.with_header(ORIGIN, "http://example.org".parse().unwrap())
		.perform()
		.unwrap();
	assert_eq!(res.status(), StatusCode::NO_CONTENT);
	let headers = res.headers();
	println!(
		"{}",
		headers
			.keys()
			.map(|name| name.as_str())
			.collect::<Vec<_>>()
			.join(",")
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_ALLOW_ORIGIN)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		origin
	);
	assert_eq!(
		headers
			.get(VARY)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		vary
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_ALLOW_CREDENTIALS)
			.and_then(|value| value.to_str().ok())
			.map(|value| value == "true")
			.unwrap_or(false),
		credentials
	);
	assert!(headers.get(ACCESS_CONTROL_MAX_AGE).is_none());
}

fn test_preflight(
	server: &TestServer,
	method: &str,
	origin: Option<&str>,
	vary: &str,
	credentials: bool,
	max_age: u64
) {
	let res = server
		.client()
		.options("http://example.org/foo")
		.with_header(ACCESS_CONTROL_REQUEST_METHOD, method.parse().unwrap())
		.with_header(ORIGIN, "http://example.org".parse().unwrap())
		.perform()
		.unwrap();
	assert_eq!(res.status(), StatusCode::NO_CONTENT);
	let headers = res.headers();
	println!(
		"{}",
		headers
			.keys()
			.map(|name| name.as_str())
			.collect::<Vec<_>>()
			.join(",")
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_ALLOW_METHODS)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		Some(method)
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_ALLOW_ORIGIN)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		origin
	);
	assert_eq!(
		headers
			.get(VARY)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		Some(vary)
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_ALLOW_CREDENTIALS)
			.and_then(|value| value.to_str().ok())
			.map(|value| value == "true")
			.unwrap_or(false),
		credentials
	);
	assert_eq!(
		headers
			.get(ACCESS_CONTROL_MAX_AGE)
			.and_then(|value| value.to_str().ok())
			.and_then(|value| value.parse().ok()),
		Some(max_age)
	);
}

fn test_preflight_headers(
	server: &TestServer,
	method: &str,
	request_headers: Option<&str>,
	allowed_headers: Option<&str>,
	vary: &str
) {
	let client = server.client();
	let mut res = client
		.options("http://example.org/foo")
		.with_header(ACCESS_CONTROL_REQUEST_METHOD, method.parse().unwrap())
		.with_header(ORIGIN, "http://example.org".parse().unwrap());
	if let Some(hdr) = request_headers {
		res = res.with_header(ACCESS_CONTROL_REQUEST_HEADERS, hdr.parse().unwrap());
	}
	let res = res.perform().unwrap();
	assert_eq!(res.status(), StatusCode::NO_CONTENT);
	let headers = res.headers();
	println!(
		"{}",
		headers
			.keys()
			.map(|name| name.as_str())
			.collect::<Vec<_>>()
			.join(",")
	);
	if let Some(hdr) = allowed_headers {
		assert_eq!(
			headers
				.get(ACCESS_CONTROL_ALLOW_HEADERS)
				.and_then(|value| value.to_str().ok())
				.as_deref(),
			Some(hdr)
		)
	} else {
		assert!(!headers.contains_key(ACCESS_CONTROL_ALLOW_HEADERS));
	}
	assert_eq!(
		headers
			.get(VARY)
			.and_then(|value| value.to_str().ok())
			.as_deref(),
		Some(vary)
	);
}

