#[test]
fn sync_methods() {
	let _ = pretty_env_logger::try_init_timed();

	let server = TestServer::new(build_simple_router(|router| {
		router.resource::<FooResource>("foo");
	}))
	.unwrap();

	test_get_response(&server, "http://localhost/foo", READ_ALL_RESPONSE);
	test_get_response(&server, "http://localhost/foo/1", READ_RESPONSE);
	test_get_response(
		&server,
		"http://localhost/foo/search?query=hello+world",
		SEARCH_RESPONSE
	);
	test_post_response(
		&server,
		"http://localhost/foo",
		r#"{"data":"hello world"}"#,
		APPLICATION_JSON,
		CREATE_RESPONSE
	);
	test_put_response(
		&server,
		"http://localhost/foo",
		r#"{"data":"hello world"}"#,
		APPLICATION_JSON,
		UPDATE_ALL_RESPONSE
	);
	test_put_response(
		&server,
		"http://localhost/foo/1",
		r#"{"data":"hello world"}"#,
		APPLICATION_JSON,
		UPDATE_RESPONSE
	);
	test_delete_response(&server, "http://localhost/foo", DELETE_ALL_RESPONSE);
	test_delete_response(&server, "http://localhost/foo/1", DELETE_RESPONSE);
}

