		APPLICATION_JSON,
		UPDATE_RESPONSE
	);
	test_delete_response(&server, "http://localhost/foo", DELETE_ALL_RESPONSE);
	test_delete_response(&server, "http://localhost/foo/1", DELETE_RESPONSE);
}

